    const value = await dataref.current.value;
    setData((value)=>{
      return value;
    });
    console.log(value)
    const user = { type: props.title, inputValue: value };
    props.handleData(user);
  }
  return (
    <div>
      <input
      ref={dataref}
        className="reg-inp"
        type="text"

        placeholder={props.title}
        onChange={changeHandle}
      />
    </div>
  );
}
